package de.nicerecord.citybuildsystem.bank;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.utils.GUIManager;
import de.nicerecord.citybuildsystem.utils.MessageManager;
import net.milkbowl.vault.economy.Economy;
import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.inventory.meta.SkullMeta;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class BankGUI {
    private final CitybuildSystem plugin;
    private final BankManager bankManager;
    private final MessageManager messageManager;
    private final Economy economy;
    private final GUIManager guiManager;

    // Deposit amounts for different items - loaded from config
    // Deposit amounts for different items - loaded from config
    private double[] nuggetAmounts;
    private double[] ingotAmounts;
    private double[] blockAmounts;

    public BankGUI(CitybuildSystem plugin, BankManager bankManager, MessageManager messageManager, Economy economy) {
        this.plugin = plugin;
        this.bankManager = bankManager;
        this.messageManager = messageManager;
        this.economy = economy;
        this.guiManager = plugin.getGUIManager();
        // Load amounts from config

        // Load amounts from config
        loadAmountsFromConfig();
    }

    private void loadAmountsFromConfig() {
        List<Integer> nuggetList = guiManager.getBankNuggetAmounts();
        // Fallback to default values if config is empty
        List<Integer> ingotList = guiManager.getBankIngotAmounts();
        List<Integer> blockList = guiManager.getBankBlockAmounts();

        // Fallback to default values if config is empty
        if (nuggetList.isEmpty()) {
            nuggetAmounts = new double[]{10, 25, 50, 100, 250};
        } else {
            nuggetAmounts = nuggetList.stream().mapToDouble(Integer::doubleValue).toArray();
        }

        if (ingotList.isEmpty()) {
            ingotAmounts = new double[]{500, 1000, 2500, 5000};
        } else {
            ingotAmounts = ingotList.stream().mapToDouble(Integer::doubleValue).toArray();
        }

        if (blockList.isEmpty()) {
            blockAmounts = new double[]{10000, 25000, 50000, 100000};
        } else {
            blockAmounts = blockList.stream().mapToDouble(Integer::doubleValue).toArray();
        }
    }
        // Fill border

        // Add main items
    public void openMainGUI(Player player) {
        String title = ChatColor.translateAlternateColorCodes('&', guiManager.getBankMainGUITitle());
        Inventory inventory = Bukkit.createInventory(null, guiManager.getBankMainGUISize(), title);
                // Player head (slot 36)

                // Deposit button (slot 20)
        // Fill border
                // Withdraw button (slot 24)
        fillBorder(inventory);
                // Info item (slot 40)

                // Close button (slot 44)
        // Add main items
        bankManager.getBalance(player.getUniqueId()).thenAccept(bankBalance -> {
            double vaultBalance = economy.getBalance(player);

            Bukkit.getScheduler().runTask(plugin, () -> {
                // Player head (slot 36)
                inventory.setItem(36, createPlayerHead(player, bankBalance, vaultBalance));

                // Deposit button (slot 20)
                inventory.setItem(20, createDepositItem());
        // Fill border

                // Withdraw button (slot 24)
                inventory.setItem(24, createWithdrawItem());

                // Info item (slot 40)
                // Player head (slot 4)
                inventory.setItem(40, createMainInfoItem());
                // Deposit items

                // Back button (slot 27)
                // Close button (slot 44)
                // Info item (slot 31)
                inventory.setItem(44, createCloseItem());
                // Close button (slot 35)

                player.openInventory(inventory);
            });
        });
    }

    public void openDepositGUI(Player player) {
        String title = ChatColor.translateAlternateColorCodes('&', guiManager.getBankDepositGUITitle());
        Inventory inventory = Bukkit.createInventory(null, guiManager.getBankDepositGUISize(), title);

        // Fill border
        // Fill border
        fillBorderSmall(inventory);

        bankManager.getBalance(player.getUniqueId()).thenAccept(bankBalance -> {
            double vaultBalance = economy.getBalance(player);
                // Player head (slot 4)

                // Withdraw items
            Bukkit.getScheduler().runTask(plugin, () -> {
                // Back button (slot 27)
                // Player head (slot 4)
                // Info item (slot 31)
                inventory.setItem(4, createPlayerHead(player, bankBalance, vaultBalance));
                // Close button (slot 35)

                // Deposit items
                addDepositItems(inventory, player);

                // Back button (slot 27)
                inventory.setItem(27, createBackItem());

                // Info item (slot 31)
                inventory.setItem(31, createDepositInfoItem());
        // Top and bottom row

                // Close button (slot 35)
                inventory.setItem(35, createCloseItem());

        // Left and right columns
                player.openInventory(inventory);
            });
        });
    }

    public void openWithdrawGUI(Player player) {
        String title = ChatColor.translateAlternateColorCodes('&', guiManager.getBankWithdrawGUITitle());
        Inventory inventory = Bukkit.createInventory(null, guiManager.getBankWithdrawGUISize(), title);
        // Top and bottom row

        // Fill border
        fillBorderSmall(inventory);

        // Left and right columns
        bankManager.getBalance(player.getUniqueId()).thenAccept(bankBalance -> {
            double vaultBalance = economy.getBalance(player);

            Bukkit.getScheduler().runTask(plugin, () -> {
                // Player head (slot 4)
                inventory.setItem(4, createPlayerHead(player, bankBalance, vaultBalance));

                // Withdraw items
                addWithdrawItems(inventory, player);

                // Back button (slot 27)
                inventory.setItem(27, createBackItem());

                // Info item (slot 31)
                inventory.setItem(31, createWithdrawInfoItem());

                // Close button (slot 35)
                inventory.setItem(35, createCloseItem());

                player.openInventory(inventory);
            });
        });
    }

    private void fillBorder(Inventory inventory) {
        ItemStack borderItem = createBorderItem();

        // Top and bottom row
        for (int i = 0; i < 9; i++) {
            inventory.setItem(i, borderItem);
            inventory.setItem(36 + i, borderItem);
        }

        // Left and right columns
        for (int i = 9; i < 36; i += 9) {
            inventory.setItem(i, borderItem);
            inventory.setItem(i + 8, borderItem);
        }
    }

    private void fillBorderSmall(Inventory inventory) {
        ItemStack borderItem = createBorderItem();

        // Top and bottom row
        for (int i = 0; i < 9; i++) {
            inventory.setItem(i, borderItem);
            inventory.setItem(27 + i, borderItem);
        }

        // Left and right columns
        for (int i = 9; i < 27; i += 9) {
            inventory.setItem(i, borderItem);
            inventory.setItem(i + 8, borderItem);
        }
    }

    private ItemStack createBorderItem() {
        Material borderMaterial = Material.valueOf(guiManager.getBorderMaterial());
        ItemStack item = new ItemStack(borderMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBorderName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBorderLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createPlayerHead(Player player, double bankBalance, double vaultBalance) {
        Material headMaterial = Material.valueOf(guiManager.getBankPlayerHeadMaterial());
        ItemStack item = new ItemStack(headMaterial);
        SkullMeta meta = (SkullMeta) item.getItemMeta();
        meta.setOwningPlayer(player);
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankPlayerHeadName()));

        List<String> configLore = guiManager.getBankPlayerHeadLore();
        List<String> lore = new ArrayList<>();

        for (String line : configLore) {
            line = line.replace("%bank_balance%", formatMoney(bankBalance))
                      .replace("%vault_balance%", formatMoney(vaultBalance));
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }

        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createDepositItem() {
        Material depositMaterial = Material.valueOf(guiManager.getBankDepositMaterial());
        ItemStack item = new ItemStack(depositMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankDepositName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankDepositLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createWithdrawItem() {
        Material withdrawMaterial = Material.valueOf(guiManager.getBankWithdrawMaterial());
        ItemStack item = new ItemStack(withdrawMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankWithdrawName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankWithdrawLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createMainInfoItem() {
        Material infoMaterial = Material.valueOf(guiManager.getBankInfoMaterial());
        ItemStack item = new ItemStack(infoMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankInfoName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankInfoLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createDepositInfoItem() {
        Material infoMaterial = Material.valueOf(guiManager.getBankInfoMaterial());
        ItemStack item = new ItemStack(infoMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankDepositInfoName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankDepositInfoLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        // Golden nuggets (slots 10-14)
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
        // Gold ingots (slots 15-16 and 19-20)
    }

    private ItemStack createWithdrawInfoItem() {
        Material infoMaterial = Material.valueOf(guiManager.getBankInfoMaterial());
        // Gold blocks (slots 21-24)
        ItemStack item = new ItemStack(infoMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankWithdrawInfoName()));

        List<String> lore = new ArrayList<>();
        // Golden nuggets (slots 10-14)
        for (String line : guiManager.getBankWithdrawInfoLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        // Gold ingots (slots 15-16 and 19-20)
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }
        // Gold blocks (slots 21-24)

    private ItemStack createCloseItem() {
        Material closeMaterial = Material.valueOf(guiManager.getCloseMaterial());
        ItemStack item = new ItemStack(closeMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getCloseName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getCloseLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createBackItem() {
        Material backMaterial = Material.valueOf(guiManager.getBankBackMaterial());
        ItemStack item = new ItemStack(backMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankBackName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankBackLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private void addDepositItems(Inventory inventory, Player player) {
        // Golden nuggets (slots 10-14)
        for (int i = 0; i < nuggetAmounts.length; i++) {
            inventory.setItem(10 + i, createDepositItem(Material.GOLD_NUGGET, nuggetAmounts[i]));
        }

        // Gold ingots (slots 15-16 and 19-20)
        inventory.setItem(15, createDepositItem(Material.GOLD_INGOT, ingotAmounts[0]));
        inventory.setItem(16, createDepositItem(Material.GOLD_INGOT, ingotAmounts[1]));
        inventory.setItem(19, createDepositItem(Material.GOLD_INGOT, ingotAmounts[2]));
        inventory.setItem(20, createDepositItem(Material.GOLD_INGOT, ingotAmounts[3]));

        // Gold blocks (slots 21-24)
        for (int i = 0; i < blockAmounts.length; i++) {
            inventory.setItem(21 + i, createDepositItem(Material.GOLD_BLOCK, blockAmounts[i]));
        }
    }

    private void addWithdrawItems(Inventory inventory, Player player) {
        // Golden nuggets (slots 10-14)
        for (int i = 0; i < nuggetAmounts.length; i++) {
            inventory.setItem(10 + i, createWithdrawItem(Material.GOLD_NUGGET, nuggetAmounts[i]));
        }

        // Gold ingots (slots 15-16 and 19-20)
        inventory.setItem(15, createWithdrawItem(Material.GOLD_INGOT, ingotAmounts[0]));
        inventory.setItem(16, createWithdrawItem(Material.GOLD_INGOT, ingotAmounts[1]));
        inventory.setItem(19, createWithdrawItem(Material.GOLD_INGOT, ingotAmounts[2]));
        inventory.setItem(20, createWithdrawItem(Material.GOLD_INGOT, ingotAmounts[3]));

        // Gold blocks (slots 21-24)
        for (int i = 0; i < blockAmounts.length; i++) {
            inventory.setItem(21 + i, createWithdrawItem(Material.GOLD_BLOCK, blockAmounts[i]));
        }
    }

    private ItemStack createDepositItem(Material material, double amount) {
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', "&e&l" + formatMoney(amount)));
        meta.setLore(Arrays.asList(ChatColor.translateAlternateColorCodes('&', "&7Klicke um " + formatMoney(amount) + " einzuzahlen")));
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createWithdrawItem(Material material, double amount) {
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', "&e&l" + formatMoney(amount)));
        meta.setLore(Arrays.asList(ChatColor.translateAlternateColorCodes('&', "&7Klicke um " + formatMoney(amount) + " auszuzahlen")));
        item.setItemMeta(meta);
        return item;
    }

    private String formatMoney(double amount) {
        if (amount >= 1000000) {
            return String.format("%.1fM", amount / 1000000);
        } else if (amount >= 1000) {
            return String.format("%.1fK", amount / 1000);
        } else {
            return String.format("%.0f", amount);
        }
    }
}

package de.nicerecord.citybuildsystem.bank;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.utils.GUIManager;
import de.nicerecord.citybuildsystem.utils.MessageManager;
import net.milkbowl.vault.economy.Economy;
import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.inventory.meta.SkullMeta;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class BankGUI {
    private final CitybuildSystem plugin;
    private final BankManager bankManager;
    private final MessageManager messageManager;
    private final Economy economy;
    private final GUIManager guiManager;

    private double[] nuggetAmounts;
    private double[] ingotAmounts;
    private double[] blockAmounts;

    public BankGUI(CitybuildSystem plugin, BankManager bankManager, MessageManager messageManager, Economy economy) {
        this.plugin = plugin;
        this.bankManager = bankManager;
        this.messageManager = messageManager;
        this.economy = economy;
        this.guiManager = plugin.getGUIManager();

        loadAmountsFromConfig();
    }

    private void loadAmountsFromConfig() {
        List<Integer> nuggetList = guiManager.getBankNuggetAmounts();
        List<Integer> ingotList = guiManager.getBankIngotAmounts();
        List<Integer> blockList = guiManager.getBankBlockAmounts();

        if (nuggetList.isEmpty()) {
            nuggetAmounts = new double[]{10, 25, 50, 100, 250};
        } else {
            nuggetAmounts = nuggetList.stream().mapToDouble(Integer::doubleValue).toArray();
        }

        if (ingotList.isEmpty()) {
            ingotAmounts = new double[]{500, 1000, 2500, 5000};
        } else {
            ingotAmounts = ingotList.stream().mapToDouble(Integer::doubleValue).toArray();
        }

        if (blockList.isEmpty()) {
            blockAmounts = new double[]{10000, 25000, 50000, 100000};
        } else {
            blockAmounts = blockList.stream().mapToDouble(Integer::doubleValue).toArray();
        }
    }

    public void openMainGUI(Player player) {
        String title = ChatColor.translateAlternateColorCodes('&', guiManager.getBankMainGUITitle());
        Inventory inventory = Bukkit.createInventory(null, guiManager.getBankMainGUISize(), title);

        fillBorder(inventory);

        bankManager.getBalance(player.getUniqueId()).thenAccept(bankBalance -> {
            double vaultBalance = economy.getBalance(player);

            Bukkit.getScheduler().runTask(plugin, () -> {
                inventory.setItem(36, createPlayerHead(player, bankBalance, vaultBalance));

                inventory.setItem(20, createDepositItem());

                inventory.setItem(24, createWithdrawItem());

                inventory.setItem(40, createMainInfoItem());

                inventory.setItem(44, createCloseItem());

                player.openInventory(inventory);
            });
        });
    }

    public void openDepositGUI(Player player) {
        String title = ChatColor.translateAlternateColorCodes('&', guiManager.getBankDepositGUITitle());
        Inventory inventory = Bukkit.createInventory(null, guiManager.getBankDepositGUISize(), title);

        fillBorderSmall(inventory);

        bankManager.getBalance(player.getUniqueId()).thenAccept(bankBalance -> {
            double vaultBalance = economy.getBalance(player);

            Bukkit.getScheduler().runTask(plugin, () -> {
                inventory.setItem(4, createPlayerHead(player, bankBalance, vaultBalance));

                addDepositItems(inventory, player);

                inventory.setItem(27, createBackItem());

                inventory.setItem(31, createDepositInfoItem());

                inventory.setItem(35, createCloseItem());

                player.openInventory(inventory);
            });
        });
    }

    public void openWithdrawGUI(Player player) {
        String title = ChatColor.translateAlternateColorCodes('&', guiManager.getBankWithdrawGUITitle());
        Inventory inventory = Bukkit.createInventory(null, guiManager.getBankWithdrawGUISize(), title);

        fillBorderSmall(inventory);

        bankManager.getBalance(player.getUniqueId()).thenAccept(bankBalance -> {
            double vaultBalance = economy.getBalance(player);

            Bukkit.getScheduler().runTask(plugin, () -> {
                inventory.setItem(4, createPlayerHead(player, bankBalance, vaultBalance));

                addWithdrawItems(inventory, player);

                inventory.setItem(27, createBackItem());

                inventory.setItem(31, createWithdrawInfoItem());

                inventory.setItem(35, createCloseItem());

                player.openInventory(inventory);
            });
        });
    }

    private void fillBorder(Inventory inventory) {
        ItemStack borderItem = createBorderItem();

        for (int i = 0; i < 9; i++) {
            inventory.setItem(i, borderItem);
            inventory.setItem(36 + i, borderItem);
        }

        for (int i = 9; i < 36; i += 9) {
            inventory.setItem(i, borderItem);
            inventory.setItem(i + 8, borderItem);
        }
    }

    private void fillBorderSmall(Inventory inventory) {
        ItemStack borderItem = createBorderItem();

        for (int i = 0; i < 9; i++) {
            inventory.setItem(i, borderItem);
            inventory.setItem(27 + i, borderItem);
        }

        for (int i = 9; i < 27; i += 9) {
            inventory.setItem(i, borderItem);
            inventory.setItem(i + 8, borderItem);
        }
    }

    private ItemStack createBorderItem() {
        Material borderMaterial = Material.valueOf(guiManager.getBorderMaterial());
        ItemStack item = new ItemStack(borderMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBorderName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBorderLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createPlayerHead(Player player, double bankBalance, double vaultBalance) {
        Material headMaterial = Material.valueOf(guiManager.getBankPlayerHeadMaterial());
        ItemStack item = new ItemStack(headMaterial);
        SkullMeta meta = (SkullMeta) item.getItemMeta();
        meta.setOwningPlayer(player);
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankPlayerHeadName()));

        List<String> configLore = guiManager.getBankPlayerHeadLore();
        List<String> lore = new ArrayList<>();

        for (String line : configLore) {
            line = line.replace("%bank_balance%", formatMoney(bankBalance))
                      .replace("%vault_balance%", formatMoney(vaultBalance));
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }

        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createDepositItem() {
        Material depositMaterial = Material.valueOf(guiManager.getBankDepositMaterial());
        ItemStack item = new ItemStack(depositMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankDepositName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankDepositLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createWithdrawItem() {
        Material withdrawMaterial = Material.valueOf(guiManager.getBankWithdrawMaterial());
        ItemStack item = new ItemStack(withdrawMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankWithdrawName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankWithdrawLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createMainInfoItem() {
        Material infoMaterial = Material.valueOf(guiManager.getBankInfoMaterial());
        ItemStack item = new ItemStack(infoMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankInfoName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankInfoLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createDepositInfoItem() {
        Material infoMaterial = Material.valueOf(guiManager.getBankInfoMaterial());
        ItemStack item = new ItemStack(infoMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankDepositInfoName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankDepositInfoLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createWithdrawInfoItem() {
        Material infoMaterial = Material.valueOf(guiManager.getBankInfoMaterial());
        ItemStack item = new ItemStack(infoMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankWithdrawInfoName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankWithdrawInfoLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createCloseItem() {
        Material closeMaterial = Material.valueOf(guiManager.getCloseMaterial());
        ItemStack item = new ItemStack(closeMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getCloseName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getCloseLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createBackItem() {
        Material backMaterial = Material.valueOf(guiManager.getBankBackMaterial());
        ItemStack item = new ItemStack(backMaterial);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBankBackName()));

        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBankBackLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private void addDepositItems(Inventory inventory, Player player) {
        for (int i = 0; i < nuggetAmounts.length; i++) {
            inventory.setItem(10 + i, createDepositItem(Material.GOLD_NUGGET, nuggetAmounts[i]));
        }

        inventory.setItem(15, createDepositItem(Material.GOLD_INGOT, ingotAmounts[0]));
        inventory.setItem(16, createDepositItem(Material.GOLD_INGOT, ingotAmounts[1]));
        inventory.setItem(19, createDepositItem(Material.GOLD_INGOT, ingotAmounts[2]));
        inventory.setItem(20, createDepositItem(Material.GOLD_INGOT, ingotAmounts[3]));

        for (int i = 0; i < blockAmounts.length; i++) {
            inventory.setItem(21 + i, createDepositItem(Material.GOLD_BLOCK, blockAmounts[i]));
        }
    }

    private void addWithdrawItems(Inventory inventory, Player player) {
        for (int i = 0; i < nuggetAmounts.length; i++) {
            inventory.setItem(10 + i, createWithdrawItem(Material.GOLD_NUGGET, nuggetAmounts[i]));
        }

        inventory.setItem(15, createWithdrawItem(Material.GOLD_INGOT, ingotAmounts[0]));
        inventory.setItem(16, createWithdrawItem(Material.GOLD_INGOT, ingotAmounts[1]));
        inventory.setItem(19, createWithdrawItem(Material.GOLD_INGOT, ingotAmounts[2]));
        inventory.setItem(20, createWithdrawItem(Material.GOLD_INGOT, ingotAmounts[3]));

        for (int i = 0; i < blockAmounts.length; i++) {
            inventory.setItem(21 + i, createWithdrawItem(Material.GOLD_BLOCK, blockAmounts[i]));
        }
    }

    private ItemStack createDepositItem(Material material, double amount) {
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', "&e&l" + formatMoney(amount)));
        meta.setLore(Arrays.asList(ChatColor.translateAlternateColorCodes('&', "&7Klicke um " + formatMoney(amount) + " einzuzahlen")));
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createWithdrawItem(Material material, double amount) {
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', "&e&l" + formatMoney(amount)));
        meta.setLore(Arrays.asList(ChatColor.translateAlternateColorCodes('&', "&7Klicke um " + formatMoney(amount) + " auszuzahlen")));
        item.setItemMeta(meta);
        return item;
    }

    private String formatMoney(double amount) {
        if (amount >= 1000000) {
            return String.format("%.1fM", amount / 1000000);
        } else if (amount >= 1000) {
            return String.format("%.1fK", amount / 1000);
        } else {
            return String.format("%.0f", amount);
        }
    }
}

package de.nicerecord.citybuildsystem.bank;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import net.milkbowl.vault.economy.Economy;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.InventoryClickEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;

public class BankGUIListener implements Listener {
    private final CitybuildSystem plugin;
    private final BankManager bankManager;
    private final MessageManager messageManager;
    private final Economy economy;
    private final BankGUI bankGUI;
    public BankGUIListener(CitybuildSystem plugin, BankManager bankManager, MessageManager messageManager, Economy economy) {
        this.plugin = plugin;
        this.bankManager = bankManager;
        this.messageManager = messageManager;
        this.economy = economy;
        this.bankGUI = new BankGUI(plugin, bankManager, messageManager, economy);
    }

    public void onInventoryClick(InventoryClickEvent event) {
        if (!(event.getWhoClicked() instanceof Player)) return;

        Player player = (Player) event.getWhoClicked();
        String title = event.getView().getTitle();

        if (!title.contains("Bank") && !title.contains("einzahlen") && !title.contains("auszahlen")) {
            return;
        }

        event.setCancelled(true);
        if (!title.contains("Bank") && !title.contains("einzahlen") && !title.contains("auszahlen")) {
        if (meta == null || meta.getDisplayName() == null) return;

        String displayName = meta.getDisplayName();

        if (title.contains("Deine Bank")) {
            handleMainGUI(player, displayName, clickedItem);
        } else if (title.contains("einzahlen")) {
            handleDepositGUI(player, displayName, clickedItem);
        } else if (title.contains("auszahlen")) {
            handleWithdrawGUI(player, displayName, clickedItem);
        }
    }

    private void handleMainGUI(Player player, String displayName, ItemStack item) {
        if (title.contains("Deine Bank")) {
            bankGUI.openDepositGUI(player);
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lGELD AUSZAHLEN"))) {
            bankGUI.openWithdrawGUI(player);
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&c&lSCHLIEEN"))) {
            player.closeInventory();
        } else if (title.contains("einzahlen")) {
    }
        } else if (title.contains("auszahlen")) {
    private void handleDepositGUI(Player player, String displayName, ItemStack item) {
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&c&lZURCK"))) {
            bankGUI.openMainGUI(player);
            return;
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&c&lSCHLIEEN"))) {
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lGELD EINZAHLEN"))) {
            return;
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lGELD AUSZAHLEN"))) {
                   displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lDEIN ACCOUNT"))) {
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&c&lSCHLIEEN"))) {
        }

        Material material = item.getType();
        if (material == Material.GOLD_NUGGET || material == Material.GOLD_INGOT || material == Material.GOLD_BLOCK) {
            double amount = parseAmountFromDisplayName(displayName);
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&c&lZURCK"))) {
                performDeposit(player, amount);
            }
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&c&lSCHLIEEN"))) {
    }

        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lINFO")) ||
                   displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lDEIN ACCOUNT"))) {
            bankGUI.openMainGUI(player);
            return;
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&c&lSCHLIEEN"))) {
            player.closeInventory();
            return;
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lINFO")) ||
                   displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lDEIN ACCOUNT"))) {
            return;
        }

        Material material = item.getType();
        if (material == Material.GOLD_NUGGET || material == Material.GOLD_INGOT || material == Material.GOLD_BLOCK) {
            double amount = parseAmountFromDisplayName(displayName);
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&c&lZURCK"))) {
                performWithdraw(player, amount);
            }
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&c&lSCHLIEEN"))) {
    }

        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lINFO")) ||
                   displayName.equals(ChatColor.translateAlternateColorCodes('&', "&e&lDEIN ACCOUNT"))) {

        try {
            if (stripped.endsWith("K")) {
                return Double.parseDouble(stripped.replace("K", "")) * 1000;
            } else if (stripped.endsWith("M")) {
                return Double.parseDouble(stripped.replace("M", "")) * 1000000;
            } else {
                return Double.parseDouble(stripped);
            }
        } catch (NumberFormatException e) {
            return 0;
        }
    }


        if (amount > 100000) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                messageManager.getMessage("bank.deposit-limit")));
            return;
        }

        if (economy.getBalance(player) < amount) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                messageManager.getMessage("bank.insufficient-funds")));
            return;
        }

        bankManager.deposit(player, amount).thenAccept(success -> {
            if (success) {
                economy.withdrawPlayer(player, amount);
            player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    messageManager.getMessage("bank.deposit-success")
                        .replace("%amount%", formatMoney(amount))));

                plugin.getServer().getScheduler().runTaskLater(plugin, () -> {
                    bankGUI.openDepositGUI(player);
            player.sendMessage(ChatColor.translateAlternateColorCodes('&',
            } else {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    messageManager.getMessage("bank.deposit-failed")));
            }
        });
    }

    private void performWithdraw(Player player, double amount) {
        if (amount > 1000000) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&',

            return;
        }

        bankManager.getBalance(player.getUniqueId()).thenAccept(bankBalance -> {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    messageManager.getMessage("bank.insufficient-bank-funds")));
                return;
            }

            bankManager.withdraw(player, amount).thenAccept(success -> {
                if (success) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                        messageManager.getMessage("bank.withdraw-success")
                            .replace("%amount%", formatMoney(amount))));

                    plugin.getServer().getScheduler().runTaskLater(plugin, () -> {
                        bankGUI.openWithdrawGUI(player);
                player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                } else {
                    player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                        messageManager.getMessage("bank.withdraw-failed")));
                }
            });
        });
    }

    private String formatMoney(double amount) {
        if (amount >= 1000000) {

        } else if (amount >= 1000) {
            return String.format("%.1fK", amount / 1000);
        } else {
            return String.format("%.0f", amount);
                    player.sendMessage(ChatColor.translateAlternateColorCodes('&',
    }
}

package de.nicerecord.citybuildsystem.bank;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.utils.GUIManager;
import de.nicerecord.citybuildsystem.utils.MessageManager;
import net.milkbowl.vault.economy.Economy;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.InventoryClickEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;

public class BankGUIListener implements Listener {
    private final CitybuildSystem plugin;
    private final BankManager bankManager;
    private final MessageManager messageManager;
    private final Economy economy;
    private final GUIManager guiManager;
    private final BankGUI bankGUI;

    public BankGUIListener(CitybuildSystem plugin, BankManager bankManager, MessageManager messageManager, Economy economy) {
        this.plugin = plugin;
        this.bankManager = bankManager;
        this.messageManager = messageManager;
        this.economy = economy;
        this.guiManager = plugin.getGUIManager();
        this.bankGUI = new BankGUI(plugin, bankManager, messageManager, economy);
    }

    @EventHandler
    public void onInventoryClick(InventoryClickEvent event) {
        if (!(event.getWhoClicked() instanceof Player)) return;

        Player player = (Player) event.getWhoClicked();
        String title = event.getView().getTitle();

        String mainTitle = ChatColor.translateAlternateColorCodes('&', guiManager.getBankMainGUITitle());
        String depositTitle = ChatColor.translateAlternateColorCodes('&', guiManager.getBankDepositGUITitle());
        String withdrawTitle = ChatColor.translateAlternateColorCodes('&', guiManager.getBankWithdrawGUITitle());

        if (!title.equals(mainTitle) && !title.equals(depositTitle) && !title.equals(withdrawTitle)) {
            return;
        }

        event.setCancelled(true);

        ItemStack clickedItem = event.getCurrentItem();
        if (clickedItem == null || clickedItem.getType() == Material.AIR) return;

        ItemMeta meta = clickedItem.getItemMeta();
        if (meta == null || meta.getDisplayName() == null) return;

        String displayName = meta.getDisplayName();

        if (title.equals(mainTitle)) {
            handleMainGUI(player, displayName, clickedItem);
        } else if (title.equals(depositTitle)) {
            handleDepositGUI(player, displayName, clickedItem);
        } else if (title.equals(withdrawTitle)) {
            handleWithdrawGUI(player, displayName, clickedItem);
        }
    }

    private void handleMainGUI(Player player, String displayName, ItemStack item) {
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getBankDepositName()))) {
            bankGUI.openDepositGUI(player);
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getBankWithdrawName()))) {
            bankGUI.openWithdrawGUI(player);
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getCloseName()))) {
            player.closeInventory();
        }
    }

    private void handleDepositGUI(Player player, String displayName, ItemStack item) {
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getBankBackName()))) {
            bankGUI.openMainGUI(player);
            return;
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getCloseName()))) {
            player.closeInventory();
            return;
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getBankDepositInfoName())) ||
                   displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getBankPlayerHeadName()))) {
            return;
        }

        Material material = item.getType();
        if (material == Material.GOLD_NUGGET || material == Material.GOLD_INGOT || material == Material.GOLD_BLOCK) {
            double amount = parseAmountFromDisplayName(displayName);
            if (amount > 0) {
                performDeposit(player, amount);
            }
        }
    }

    private void handleWithdrawGUI(Player player, String displayName, ItemStack item) {
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getBankBackName()))) {
            bankGUI.openMainGUI(player);
            return;
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getCloseName()))) {
            player.closeInventory();
            return;
        } else if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getBankWithdrawInfoName())) ||
                   displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getBankPlayerHeadName()))) {
            return;
        }

        Material material = item.getType();
        if (material == Material.GOLD_NUGGET || material == Material.GOLD_INGOT || material == Material.GOLD_BLOCK) {
            double amount = parseAmountFromDisplayName(displayName);
            if (amount > 0) {
                performWithdraw(player, amount);
            }
        }
    }

    private double parseAmountFromDisplayName(String displayName) {
        String stripped = ChatColor.stripColor(displayName);
        
        try {
            if (stripped.endsWith("K")) {
                return Double.parseDouble(stripped.replace("K", "")) * 1000;
            } else if (stripped.endsWith("M")) {
                return Double.parseDouble(stripped.replace("M", "")) * 1000000;
            } else {
                return Double.parseDouble(stripped);
            }
        } catch (NumberFormatException e) {
            return 0;
        }
    }

    private void performDeposit(Player player, double amount) {
        if (amount > 100000) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', 
                messageManager.getMessage("bank.deposit-limit")));
            return;
        }

        if (economy.getBalance(player) < amount) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', 
                messageManager.getMessage("bank.insufficient-funds")));
            return;
        }

        bankManager.deposit(player, amount).thenAccept(success -> {
            if (success) {
                economy.withdrawPlayer(player, amount);
                player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    messageManager.getMessage("bank.deposit-success")
                        .replace("%amount%", formatMoney(amount))));
                
                plugin.getServer().getScheduler().runTaskLater(plugin, () -> {
                    bankGUI.openDepositGUI(player);
                }, 1L);
            } else {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&', 
                    messageManager.getMessage("bank.deposit-failed")));
            }
        });
    }

    private void performWithdraw(Player player, double amount) {
        if (amount > 1000000) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', 
                messageManager.getMessage("bank.withdraw-limit")));
            return;
        }

        bankManager.getBalance(player.getUniqueId()).thenAccept(bankBalance -> {
            if (bankBalance < amount) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&', 
                    messageManager.getMessage("bank.insufficient-bank-funds")));
                return;
            }

            bankManager.withdraw(player, amount).thenAccept(success -> {
                if (success) {
                    economy.depositPlayer(player, amount);
                    player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                        messageManager.getMessage("bank.withdraw-success")
                            .replace("%amount%", formatMoney(amount))));
                    
                    plugin.getServer().getScheduler().runTaskLater(plugin, () -> {
                        bankGUI.openWithdrawGUI(player);
                    }, 1L);
                } else {
                    player.sendMessage(ChatColor.translateAlternateColorCodes('&', 
                        messageManager.getMessage("bank.withdraw-failed")));
                }
            });
        });
    }

    private String formatMoney(double amount) {
        if (amount >= 1000000) {
            return String.format("%.1fM", amount / 1000000);
        } else if (amount >= 1000) {
            return String.format("%.1fK", amount / 1000);
        } else {
            return String.format("%.0f", amount);
        }
    }
}

package de.nicerecord.citybuildsystem.bank;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import org.bukkit.entity.Player;

import java.io.File;
import java.sql.*;
import java.util.UUID;
import java.util.concurrent.CompletableFuture;

public class BankManager {
    private final CitybuildSystem plugin;
    private Connection connection;

    public BankManager(CitybuildSystem plugin) {
        this.plugin = plugin;
        initDatabase();
    }

    private void initDatabase() {
        try {
            File dataFolder = plugin.getDataFolder();
            if (!dataFolder.exists()) {
                dataFolder.mkdirs();
            }

            String dbPath = new File(dataFolder, "bank.db").getAbsolutePath();
            connection = DriverManager.getConnection("jdbc:sqlite:" + dbPath);

            String createTable = "CREATE TABLE IF NOT EXISTS bank_accounts (" +
                    "id INTEGER PRIMARY KEY AUTOINCREMENT," +
                    "player_uuid TEXT NOT NULL UNIQUE," +
                    "player_name TEXT NOT NULL," +
                    "balance REAL NOT NULL DEFAULT 0.0," +
                    "created_at INTEGER NOT NULL DEFAULT (strftime('%s', 'now'))," +
                    "updated_at INTEGER NOT NULL DEFAULT (strftime('%s', 'now'))" +
                    ")";

            try (PreparedStatement stmt = connection.prepareStatement(createTable)) {
                stmt.execute();
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("Fehler beim Initialisieren der Bank-Datenbank: " + e.getMessage());
        }
    }

    public CompletableFuture<Double> getBalance(UUID playerUUID) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "SELECT balance FROM bank_accounts WHERE player_uuid = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, playerUUID.toString());
                    try (ResultSet rs = stmt.executeQuery()) {
                        if (rs.next()) {
                            return rs.getDouble("balance");
                        }
                    }
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Abrufen des Bank-Guthabens: " + e.getMessage());
            }
            return 0.0;
        });
    }

    public CompletableFuture<Double> getBalance(String playerName) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "SELECT balance FROM bank_accounts WHERE LOWER(player_name) = LOWER(?)";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, playerName);
                    try (ResultSet rs = stmt.executeQuery()) {
                        if (rs.next()) {
                            return rs.getDouble("balance");
                        }
                    }
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Abrufen des Bank-Guthabens: " + e.getMessage());
            }
            return 0.0;
        });
    }

    public CompletableFuture<Boolean> deposit(Player player, double amount) {
        if (amount <= 0) return CompletableFuture.completedFuture(false);

        return CompletableFuture.supplyAsync(() -> {
            try {
                // Check if account exists, if not create it
                // Check if account exists, if not create it
                createAccountIfNotExists(player);

                String sql = "UPDATE bank_accounts SET balance = balance + ?, updated_at = strftime('%s', 'now') WHERE player_uuid = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setDouble(1, amount);
                    stmt.setString(2, player.getUniqueId().toString());
                    int affected = stmt.executeUpdate();
                    return affected > 0;
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Einzahlen: " + e.getMessage());
                return false;
            }
        });
    }

    public CompletableFuture<Boolean> withdraw(Player player, double amount) {
        if (amount <= 0) return CompletableFuture.completedFuture(false);

        return CompletableFuture.supplyAsync(() -> {
                // Check current balance
            try {
                // Check current balance
                double currentBalance = getBalance(player.getUniqueId()).join();
                if (currentBalance < amount) {
                    return false;
                }

                String sql = "UPDATE bank_accounts SET balance = balance - ?, updated_at = strftime('%s', 'now') WHERE player_uuid = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setDouble(1, amount);
                    stmt.setString(2, player.getUniqueId().toString());
                    int affected = stmt.executeUpdate();
                    return affected > 0;
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Auszahlen: " + e.getMessage());
                return false;
            }
        });
    }

    private void createAccountIfNotExists(Player player) throws SQLException {
        String checkSql = "SELECT COUNT(*) FROM bank_accounts WHERE player_uuid = ?";
        try (PreparedStatement checkStmt = connection.prepareStatement(checkSql)) {
                    // Account doesn't exist, create it
            checkStmt.setString(1, player.getUniqueId().toString());
            try (ResultSet rs = checkStmt.executeQuery()) {
                if (rs.next() && rs.getInt(1) == 0) {
                    // Account doesn't exist, create it
                    String insertSql = "INSERT INTO bank_accounts (player_uuid, player_name, balance) VALUES (?, ?, 0.0)";
                    try (PreparedStatement insertStmt = connection.prepareStatement(insertSql)) {
                    // Update player name in case it changed
                        insertStmt.setString(1, player.getUniqueId().toString());
                        insertStmt.setString(2, player.getName());
                        insertStmt.executeUpdate();
                    }
                } else {
                    // Update player name in case it changed
                    String updateSql = "UPDATE bank_accounts SET player_name = ? WHERE player_uuid = ?";
                    try (PreparedStatement updateStmt = connection.prepareStatement(updateSql)) {
                        updateStmt.setString(1, player.getName());
                        updateStmt.setString(2, player.getUniqueId().toString());
                        updateStmt.executeUpdate();
                    }
                }
            }
        }
    }

    public void close() {
        try {
            if (connection != null && !connection.isClosed()) {
                connection.close();
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("Fehler beim Schlieen der Bank-Datenbankverbindung: " + e.getMessage());
        }
    }
}

package de.nicerecord.citybuildsystem.bank;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import org.bukkit.entity.Player;

import java.io.File;
import java.sql.*;
import java.util.UUID;
import java.util.concurrent.CompletableFuture;

public class BankManager {
    private final CitybuildSystem plugin;
    private Connection connection;

    public BankManager(CitybuildSystem plugin) {
        this.plugin = plugin;
        initDatabase();
    }

    private void initDatabase() {
        try {
            File dataFolder = plugin.getDataFolder();
            if (!dataFolder.exists()) {
                dataFolder.mkdirs();
            }

            String dbPath = new File(dataFolder, "bank.db").getAbsolutePath();
            connection = DriverManager.getConnection("jdbc:sqlite:" + dbPath);

            String createTable = "CREATE TABLE IF NOT EXISTS bank_accounts (" +
                    "id INTEGER PRIMARY KEY AUTOINCREMENT," +
                    "player_uuid TEXT NOT NULL UNIQUE," +
                    "player_name TEXT NOT NULL," +
                    "balance REAL NOT NULL DEFAULT 0.0," +
                    "created_at INTEGER NOT NULL DEFAULT (strftime('%s', 'now'))," +
                    "updated_at INTEGER NOT NULL DEFAULT (strftime('%s', 'now'))" +
                    ")";

            try (PreparedStatement stmt = connection.prepareStatement(createTable)) {
                stmt.execute();
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("Fehler beim Initialisieren der Bank-Datenbank: " + e.getMessage());
        }
    }

    public CompletableFuture<Double> getBalance(UUID playerUUID) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "SELECT balance FROM bank_accounts WHERE player_uuid = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, playerUUID.toString());
                    try (ResultSet rs = stmt.executeQuery()) {
                        if (rs.next()) {
                            return rs.getDouble("balance");
                        }
                    }
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Abrufen des Bank-Guthabens: " + e.getMessage());
            }
            return 0.0;
        });
    }

    public CompletableFuture<Double> getBalance(String playerName) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "SELECT balance FROM bank_accounts WHERE LOWER(player_name) = LOWER(?)";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, playerName);
                    try (ResultSet rs = stmt.executeQuery()) {
                        if (rs.next()) {
                            return rs.getDouble("balance");
                        }
                    }
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Abrufen des Bank-Guthabens: " + e.getMessage());
            }
            return 0.0;
        });
    }

    public CompletableFuture<Boolean> deposit(Player player, double amount) {
        if (amount <= 0) return CompletableFuture.completedFuture(false);

        return CompletableFuture.supplyAsync(() -> {
            try {
                createAccountIfNotExists(player);

                String sql = "UPDATE bank_accounts SET balance = balance + ?, updated_at = strftime('%s', 'now') WHERE player_uuid = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setDouble(1, amount);
                    stmt.setString(2, player.getUniqueId().toString());
                    int affected = stmt.executeUpdate();
                    return affected > 0;
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Einzahlen: " + e.getMessage());
                return false;
            }
        });
    }

    public CompletableFuture<Boolean> withdraw(Player player, double amount) {
        if (amount <= 0) return CompletableFuture.completedFuture(false);

        return CompletableFuture.supplyAsync(() -> {
            try {
                double currentBalance = getBalance(player.getUniqueId()).join();
                if (currentBalance < amount) {
                    return false;
                }

                String sql = "UPDATE bank_accounts SET balance = balance - ?, updated_at = strftime('%s', 'now') WHERE player_uuid = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setDouble(1, amount);
                    stmt.setString(2, player.getUniqueId().toString());
                    int affected = stmt.executeUpdate();
                    return affected > 0;
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Auszahlen: " + e.getMessage());
                return false;
            }
        });
    }

    private void createAccountIfNotExists(Player player) throws SQLException {
        String checkSql = "SELECT COUNT(*) FROM bank_accounts WHERE player_uuid = ?";
        try (PreparedStatement checkStmt = connection.prepareStatement(checkSql)) {
            checkStmt.setString(1, player.getUniqueId().toString());
            try (ResultSet rs = checkStmt.executeQuery()) {
                if (rs.next() && rs.getInt(1) == 0) {
                    String insertSql = "INSERT INTO bank_accounts (player_uuid, player_name, balance) VALUES (?, ?, 0.0)";
                    try (PreparedStatement insertStmt = connection.prepareStatement(insertSql)) {
                        insertStmt.setString(1, player.getUniqueId().toString());
                        insertStmt.setString(2, player.getName());
                        insertStmt.executeUpdate();
                    }
                } else {
                    String updateSql = "UPDATE bank_accounts SET player_name = ? WHERE player_uuid = ?";
                    try (PreparedStatement updateStmt = connection.prepareStatement(updateSql)) {
                        updateStmt.setString(1, player.getName());
                        updateStmt.setString(2, player.getUniqueId().toString());
                        updateStmt.executeUpdate();
                    }
                }
            }
        }
    }

    public void close() {
        try {
            if (connection != null && !connection.isClosed()) {
                connection.close();
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("Fehler beim Schlieen der Bank-Datenbankverbindung: " + e.getMessage());
        }
    }
}

package de.nicerecord.citybuildsystem.home;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.utils.ConfigManager;
import de.nicerecord.citybuildsystem.utils.GUIManager;
import de.nicerecord.citybuildsystem.utils.MessageManager;
import net.milkbowl.vault.economy.Economy;
import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.UUID;

public class HomeGUI {
    private final CitybuildSystem plugin;
    private final HomeManager homeManager;
    private final ConfigManager configManager;
    private final GUIManager guiManager;
    private final MessageManager messageManager;
    private final Economy economy;

    // Store current page for each player
    private final Map<UUID, Integer> playerPages = new HashMap<>();

    // Slots available for homes per page (excluding borders, navigation, info items)
    private static final int HOMES_PER_PAGE = 28;

    public HomeGUI(CitybuildSystem plugin, HomeManager homeManager, ConfigManager configManager, GUIManager guiManager, MessageManager messageManager, Economy economy) {
        this.plugin = plugin;
        this.homeManager = homeManager;
        this.configManager = configManager;
        this.guiManager = guiManager;
        this.messageManager = messageManager;
        this.economy = economy;
    }

    public void openHomesGUI(Player player) {
        openHomesGUI(player, 1);
    }

    public void openHomesGUI(Player player, int page) {
        playerPages.put(player.getUniqueId(), page);
        String title = ChatColor.translateAlternateColorCodes('&', guiManager.getHomeGUITitle());
        Inventory inventory = Bukkit.createInventory(null, guiManager.getHomeGUISize(), title);

        homeManager.getPlayerHomes(player).thenAccept(homes -> {
            Bukkit.getScheduler().runTask(plugin, () -> {
                int maxHomes = homeManager.getMaxHomesForPlayer(player);
                int configMaxHomes = configManager.getMaxHomes(); // Maximum aus Config

                // Berechne Paginierung basierend auf dem greren Wert (entweder verfgbare Homes oder Config-Maximum)
                int homesForPagination = Math.max(maxHomes, configMaxHomes);
                int totalPages = calculateTotalPages(homesForPagination);

                fillBorder(inventory);
                inventory.setItem(53, createCloseItem());

                // Add navigation items if needed (basierend auf Config-Maximum, nicht Spieler-Maximum)
                if (totalPages > 1) {
                    if (page > 1) {
                        inventory.setItem(48, createPreviousPageItem()); // Slot 48 fr vorherige Seite
                    }
                    if (page < totalPages) {
                        inventory.setItem(50, createNextPageItem()); // Slot 50 fr nchste Seite
                    }
                }

                // Info-Item immer anzeigen (ohne Seiteninformation)
                inventory.setItem(49, createInfoItem(player, homes.size()));

                addHomeItems(inventory, player, homes, page, maxHomes, configMaxHomes);
                player.openInventory(inventory);
            });
        });
    }

    private int calculateTotalPages(int maxHomes) {
        return (int) Math.ceil((double) maxHomes / HOMES_PER_PAGE);
    }

    private void fillBorder(Inventory inventory) {
        ItemStack borderItem = createBorderItem();
        for (int i = 0; i < 9; i++) {
            inventory.setItem(i, borderItem);
            inventory.setItem(45 + i, borderItem);
        }
        for (int i = 9; i < 45; i += 9) {
            inventory.setItem(i, borderItem);
            inventory.setItem(i + 8, borderItem);
        }
    }

    private ItemStack createBorderItem() {
        Material material = Material.valueOf(guiManager.getBorderMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBorderName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBorderLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createCloseItem() {
        Material material = Material.valueOf(guiManager.getCloseMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getCloseName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getCloseLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createInfoItem(Player player, int usedHomes) {
        Material material = Material.valueOf(guiManager.getInfoMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getInfoName()));
        int maxHomes = homeManager.getMaxHomesForPlayer(player);
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getInfoLore()) {
            line = line.replace("%used%", String.valueOf(usedHomes))
                      .replace("%max%", String.valueOf(maxHomes));
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private void addHomeItems(Inventory inventory, Player player, List<Home> homes, int page, int maxHomes, int configMaxHomes) {
        int startIndex = (page - 1) * HOMES_PER_PAGE;
        int endIndex = Math.min(startIndex + HOMES_PER_PAGE, maxHomes);

        // Get valid slots for home items (excluding border)
        List<Integer> validSlots = getValidSlots();
        int slotIndex = 0;

        // Zeige alle verfgbaren Home-Slots fr diese Seite
        for (int i = startIndex; i < endIndex && slotIndex < validSlots.size(); i++) {
            if (i < homes.size()) {
                // Existierendes Home
                Home home = homes.get(i);
                inventory.setItem(validSlots.get(slotIndex), createHomeItem(home));
            } else {
                // Leerer Home-Slot (kann gesetzt werden)
                inventory.setItem(validSlots.get(slotIndex), createEmptyHomeItem());
            }
            slotIndex++;
        }

        // Zeige "Nchsten Home kaufen" Button wenn Economy aktiviert ist und das Config-Maximum nicht erreicht ist
        if (configManager.isEconomyEnabled() && economy != null && slotIndex < validSlots.size()) {
            int currentMaxHomes = homeManager.getMaxHomesForPlayer(player);

            // Zeige Kauf-Button fr den nchsten Home-Slot wenn das Config-Maximum noch nicht erreicht ist
            if (currentMaxHomes < configMaxHomes) {
                // Berechne welcher Home-Slot als nchstes gekauft werden kann
                int nextHomeSlot = currentMaxHomes + 1;

                // Prfe ob der nchste kaufbare Slot auf dieser Seite angezeigt werden soll
                int nextSlotIndex = nextHomeSlot - 1; // 0-basiert
                if (nextSlotIndex >= startIndex && nextSlotIndex < startIndex + HOMES_PER_PAGE) {
                    // Berechne die richtige Slot-Position fr den Kauf-Button
                    int buyButtonSlotIndex = nextSlotIndex - startIndex;
                    if (buyButtonSlotIndex < validSlots.size()) {
                        inventory.setItem(validSlots.get(buyButtonSlotIndex), createBuyNextHomeItem(nextHomeSlot));
                    }
                }
            }
        }
    }

    private List<Integer> getValidSlots() {
        List<Integer> slots = new ArrayList<>();
        for (int row = 1; row < 5; row++) { // Rows 1-4 (skip borders)
            for (int col = 1; col < 8; col++) { // Cols 1-7 (skip borders)
                slots.add(row * 9 + col);
            }
        }
        return slots;
    }

    private ItemStack createHomeItem(Home home) {
        Material material = Material.valueOf(guiManager.getHomeSetMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        String name = guiManager.getHomeSetName().replace("%name%", home.getName());
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', name));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getHomeSetLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createEmptyHomeItem() {
        Material material = Material.valueOf(guiManager.getHomeEmptyMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeEmptyName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getHomeEmptyLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createLockedHomeItem() {
        Material material = Material.valueOf(guiManager.getHomeLockedMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeLockedName()));
        double price = configManager.getHomePrice();
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getHomeLockedLore()) {
            line = line.replace("%price%", String.valueOf((int) price));
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createPreviousPageItem() {
        Material material = Material.valueOf(guiManager.getPreviousPageMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getPreviousPageName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getPreviousPageLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createNextPageItem() {
        Material material = Material.valueOf(guiManager.getNextPageMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getNextPageName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getNextPageLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createBuyNextHomeItem(int homeSlotNumber) {
        Material material = Material.valueOf(guiManager.getHomeLockedMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();

        String name = guiManager.getHomeLockedName().replace("KAUFEN", "SLOT " + homeSlotNumber + " KAUFEN");
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', name));

        double price = configManager.getHomePrice();
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getHomeLockedLore()) {
            line = line.replace("%price%", String.valueOf((int) price))
                      .replace("Home", "Home-Slot " + homeSlotNumber);
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    public int getCurrentPage(Player player) {
        return playerPages.getOrDefault(player.getUniqueId(), 1);
    }

    public void removePlayerPage(Player player) {
        playerPages.remove(player.getUniqueId());
    }
}

package de.nicerecord.citybuildsystem.home;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.utils.ConfigManager;
import de.nicerecord.citybuildsystem.utils.GUIManager;
import de.nicerecord.citybuildsystem.utils.MessageManager;
import net.milkbowl.vault.economy.Economy;
import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.UUID;

public class HomeGUI {
    private final CitybuildSystem plugin;
    private final HomeManager homeManager;
    private final ConfigManager configManager;
    private final GUIManager guiManager;
    private final MessageManager messageManager;
    private final Economy economy;

    // Store current page for each player - STATIC damit es ber Instanzen hinweg funktioniert
    private static final Map<UUID, Integer> playerPages = new HashMap<>();

    // Slots available for homes per page (excluding borders, navigation, info items)
    private static final int HOMES_PER_PAGE = 28;

    public HomeGUI(CitybuildSystem plugin, HomeManager homeManager, ConfigManager configManager, GUIManager guiManager, MessageManager messageManager, Economy economy) {
        this.plugin = plugin;
        this.homeManager = homeManager;
        this.configManager = configManager;
        this.guiManager = guiManager;
        this.messageManager = messageManager;
        this.economy = economy;
    }

    public void openHomesGUI(Player player) {
        openHomesGUI(player, 1);
    }

    public void openHomesGUI(Player player, int page) {
        playerPages.put(player.getUniqueId(), page);
        String title = ChatColor.translateAlternateColorCodes('&', guiManager.getHomeGUITitle());
        Inventory inventory = Bukkit.createInventory(null, guiManager.getHomeGUISize(), title);

        homeManager.getPlayerHomes(player).thenAccept(homes -> {
            Bukkit.getScheduler().runTask(plugin, () -> {
                int maxHomes = homeManager.getMaxHomesForPlayer(player);
                int configMaxHomes = configManager.getMaxHomes(); // Maximum aus Config

                // Berechne Paginierung basierend auf dem greren Wert (entweder verfgbare Homes oder Config-Maximum)
                int homesForPagination = Math.max(maxHomes, configMaxHomes);
                int totalPages = calculateTotalPages(homesForPagination);

                fillBorder(inventory);
                inventory.setItem(53, createCloseItem());

                // Add navigation items if needed (basierend auf Config-Maximum, nicht Spieler-Maximum)
                if (totalPages > 1) {
                    if (page > 1) {
                        inventory.setItem(48, createPreviousPageItem()); // Slot 48 fr vorherige Seite
                    }
                    if (page < totalPages) {
                        inventory.setItem(50, createNextPageItem()); // Slot 50 fr nchste Seite
                    }
                }

                // Info-Item immer anzeigen (ohne Seiteninformation)
                inventory.setItem(49, createInfoItem(player, homes.size()));

                addHomeItems(inventory, player, homes, page, maxHomes, configMaxHomes);
                player.openInventory(inventory);
            });
        });
    }

    private int calculateTotalPages(int maxHomes) {
        return (int) Math.ceil((double) maxHomes / HOMES_PER_PAGE);
    }

    private void fillBorder(Inventory inventory) {
        ItemStack borderItem = createBorderItem();
        for (int i = 0; i < 9; i++) {
            inventory.setItem(i, borderItem);
            inventory.setItem(45 + i, borderItem);
        }
        for (int i = 9; i < 45; i += 9) {
            inventory.setItem(i, borderItem);
            inventory.setItem(i + 8, borderItem);
        }
    }

    private ItemStack createBorderItem() {
        Material material = Material.valueOf(guiManager.getBorderMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getBorderName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getBorderLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createCloseItem() {
        Material material = Material.valueOf(guiManager.getCloseMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getCloseName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getCloseLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createInfoItem(Player player, int usedHomes) {
        Material material = Material.valueOf(guiManager.getInfoMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getInfoName()));
        int maxHomes = homeManager.getMaxHomesForPlayer(player);
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getInfoLore()) {
            line = line.replace("%used%", String.valueOf(usedHomes))
                      .replace("%max%", String.valueOf(maxHomes));
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private void addHomeItems(Inventory inventory, Player player, List<Home> homes, int page, int maxHomes, int configMaxHomes) {
        int startIndex = (page - 1) * HOMES_PER_PAGE;
        int endIndex = Math.min(startIndex + HOMES_PER_PAGE, maxHomes);

        // Get valid slots for home items (excluding border)
        List<Integer> validSlots = getValidSlots();
        int slotIndex = 0;

        // Zeige alle verfgbaren Home-Slots fr diese Seite
        for (int i = startIndex; i < endIndex && slotIndex < validSlots.size(); i++) {
            if (i < homes.size()) {
                // Existierendes Home
                Home home = homes.get(i);
                inventory.setItem(validSlots.get(slotIndex), createHomeItem(home));
            } else {
                // Leerer Home-Slot (kann gesetzt werden)
                inventory.setItem(validSlots.get(slotIndex), createEmptyHomeItem());
            }
            slotIndex++;
        }

        // Zeige "Nchsten Home kaufen" Button wenn Economy aktiviert ist und das Config-Maximum nicht erreicht ist
        if (configManager.isEconomyEnabled() && economy != null && slotIndex < validSlots.size()) {
            int currentMaxHomes = homeManager.getMaxHomesForPlayer(player);

            // Zeige Kauf-Button fr den nchsten Home-Slot wenn das Config-Maximum noch nicht erreicht ist
            if (currentMaxHomes < configMaxHomes) {
                // Berechne welcher Home-Slot als nchstes gekauft werden kann
                int nextHomeSlot = currentMaxHomes + 1;

                // Prfe ob der nchste kaufbare Slot auf dieser Seite angezeigt werden soll
                int nextSlotIndex = nextHomeSlot - 1; // 0-basiert
                if (nextSlotIndex >= startIndex && nextSlotIndex < startIndex + HOMES_PER_PAGE) {
                    // Berechne die richtige Slot-Position fr den Kauf-Button
                    int buyButtonSlotIndex = nextSlotIndex - startIndex;
                    if (buyButtonSlotIndex < validSlots.size()) {
                        inventory.setItem(validSlots.get(buyButtonSlotIndex), createBuyNextHomeItem(nextHomeSlot));
                    }
                }
            }
        }
    }

    private List<Integer> getValidSlots() {
        List<Integer> slots = new ArrayList<>();
        for (int row = 1; row < 5; row++) { // Rows 1-4 (skip borders)
            for (int col = 1; col < 8; col++) { // Cols 1-7 (skip borders)
                slots.add(row * 9 + col);
            }
        }
        return slots;
    }

    private ItemStack createHomeItem(Home home) {
        Material material = Material.valueOf(guiManager.getHomeSetMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        String name = guiManager.getHomeSetName().replace("%name%", home.getName());
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', name));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getHomeSetLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createEmptyHomeItem() {
        Material material = Material.valueOf(guiManager.getHomeEmptyMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeEmptyName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getHomeEmptyLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createLockedHomeItem() {
        Material material = Material.valueOf(guiManager.getHomeLockedMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeLockedName()));
        double price = configManager.getHomePrice();
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getHomeLockedLore()) {
            line = line.replace("%price%", String.valueOf((int) price));
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createPreviousPageItem() {
        Material material = Material.valueOf(guiManager.getPreviousPageMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getPreviousPageName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getPreviousPageLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createNextPageItem() {
        Material material = Material.valueOf(guiManager.getNextPageMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', guiManager.getNextPageName()));
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getNextPageLore()) {
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    private ItemStack createBuyNextHomeItem(int homeSlotNumber) {
        Material material = Material.valueOf(guiManager.getHomeLockedMaterial());
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();

        String name = guiManager.getHomeLockedName().replace("KAUFEN", "SLOT " + homeSlotNumber + " KAUFEN");
        meta.setDisplayName(ChatColor.translateAlternateColorCodes('&', name));

        double price = configManager.getHomePrice();
        List<String> lore = new ArrayList<>();
        for (String line : guiManager.getHomeLockedLore()) {
            line = line.replace("%price%", String.valueOf((int) price))
                      .replace("Home", "Home-Slot " + homeSlotNumber);
            lore.add(ChatColor.translateAlternateColorCodes('&', line));
        }
        meta.setLore(lore);
        item.setItemMeta(meta);
        return item;
    }

    public int getCurrentPage(Player player) {
        return playerPages.getOrDefault(player.getUniqueId(), 1);
    }

    public void removePlayerPage(Player player) {
        playerPages.remove(player.getUniqueId());
    }
}

package de.nicerecord.citybuildsystem.home;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.utils.ConfigManager;
import de.nicerecord.citybuildsystem.utils.GUIManager;
import de.nicerecord.citybuildsystem.utils.MessageManager;
import net.milkbowl.vault.economy.Economy;
import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.ClickType;
import org.bukkit.event.inventory.InventoryClickEvent;
import org.bukkit.event.player.AsyncPlayerChatEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;

import java.util.HashMap;
import java.util.Map;
import java.util.UUID;

public class HomeGUIListener implements Listener {
    private final CitybuildSystem plugin;
    private final HomeManager homeManager;
    private final ConfigManager configManager;
    private final GUIManager guiManager;
    private final MessageManager messageManager;
    private final Economy economy;
    private final SetHomeCommand setHomeCommand;
    private final Map<UUID, Boolean> waitingForHomeName = new HashMap<>();

    public HomeGUIListener(CitybuildSystem plugin, HomeManager homeManager, ConfigManager configManager,
                          GUIManager guiManager, MessageManager messageManager, Economy economy, SetHomeCommand setHomeCommand) {
        this.plugin = plugin;
        this.homeManager = homeManager;
        this.configManager = configManager;
        this.guiManager = guiManager;
        this.messageManager = messageManager;
        this.economy = economy;
        this.setHomeCommand = setHomeCommand;
    }

    @EventHandler
    public void onInventoryClick(InventoryClickEvent event) {
        if (!(event.getWhoClicked() instanceof Player)) return;

        Player player = (Player) event.getWhoClicked();
        String guiTitle = ChatColor.translateAlternateColorCodes('&', guiManager.getHomeGUITitle());

        if (!event.getView().getTitle().equals(guiTitle)) return;

        event.setCancelled(true);

        ItemStack clickedItem = event.getCurrentItem();
        if (clickedItem == null || clickedItem.getType() == Material.AIR) return;

        ItemMeta meta = clickedItem.getItemMeta();
        if (meta == null || meta.getDisplayName() == null) return;

        String displayName = meta.getDisplayName();

        // Handle close button
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getCloseName()))) {
            player.closeInventory();
            return;
        }

        // Handle navigation - Previous page
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getPreviousPageName()))) {
            HomeGUI homeGUI = new HomeGUI(plugin, homeManager, configManager, guiManager, messageManager, economy);
            int currentPage = homeGUI.getCurrentPage(player);
            if (currentPage > 1) {
                homeGUI.openHomesGUI(player, currentPage - 1);
            }
            return;
        }

        // Handle navigation - Next page
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getNextPageName()))) {
            HomeGUI homeGUI = new HomeGUI(plugin, homeManager, configManager, guiManager, messageManager, economy);
            int currentPage = homeGUI.getCurrentPage(player);
            int configMaxHomes = configManager.getMaxHomes(); // Verwende Config-Maximum
            int totalPages = (int) Math.ceil((double) configMaxHomes / 28); // HOMES_PER_PAGE
            if (currentPage < totalPages) {
                homeGUI.openHomesGUI(player, currentPage + 1);
            }
            return;
        }

        // Handle info item (do nothing)
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getInfoName()))) {
            return;
        }

        // Handle empty home slots
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeEmptyName()))) {
            player.closeInventory();

            if (!hasPermissionToSetHome(player)) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.no-permission")));
                return;
            }

            waitingForHomeName.put(player.getUniqueId(), true);
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.enter-name")));
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.enter-name-cancel")));
            return;
        }

        // Handle locked home slots (both old and new buy-next-home items)
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeLockedName())) ||
            displayName.contains("SLOT") && displayName.contains("KAUFEN")) {
            if (!configManager.isEconomyEnabled() || economy == null) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("bank.economy-not-available")));
                return;
            }

            int currentMaxHomes = homeManager.getMaxHomesForPlayer(player);
            int configMaxHomes = configManager.getMaxHomes();

            // Prfe ob bereits das Maximum erreicht ist
            if (currentMaxHomes >= configMaxHomes) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.max-homes-purchased")));
                return;
            }

            double price = configManager.getHomePrice();

            // Prfe ob genug Geld vorhanden ist
            if (economy.getBalance(player) < price) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    messageManager.getMessage("home.insufficient-funds").replace("%price%", String.valueOf((int) price))));
                return;
            }

            // Fhre den Kauf durch
            homeManager.purchaseHomeSlot(player, economy).thenAccept(success -> {
                Bukkit.getScheduler().runTask(plugin, () -> {
                    if (success) {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                            messageManager.getMessage("home.purchase-success").replace("%price%", String.valueOf((int) price))));

                        // Schliee das GUI und ffne es erneut um die nderungen zu zeigen
                        player.closeInventory();
                        Bukkit.getScheduler().runTaskLater(plugin, () -> {
                            HomeGUI homeGUI = new HomeGUI(plugin, homeManager, configManager, guiManager, messageManager, economy);
                            int currentPage = homeGUI.getCurrentPage(player);
                            homeGUI.openHomesGUI(player, currentPage);
                        }, 5L);
                    } else {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.purchase-failed")));
                    }
                });
            });
            return;
        }

        // Handle existing home items
        if (displayName.startsWith(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeSetName().replace("%name%", "")))) {
            String homeName = ChatColor.stripColor(displayName).replace("HOME ", "");

            if (event.getClick() == ClickType.LEFT) {
                player.closeInventory();
                plugin.getServer().dispatchCommand(player, "home " + homeName);
            } else if (event.getClick() == ClickType.RIGHT) {
                homeManager.deleteHome(player, homeName).thenAccept(success -> {
                    if (success) {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                            messageManager.getMessage("home.delete-success").replace("%home%", homeName)));
                        plugin.getServer().getScheduler().runTaskLater(plugin, () -> {
                            HomeGUI homeGUI = new HomeGUI(plugin, homeManager, configManager, guiManager, messageManager, economy);
                            int currentPage = homeGUI.getCurrentPage(player);
                            homeGUI.openHomesGUI(player, currentPage);
                        }, 1L);
                    } else {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.error")));
                    }
                });
            }
        }
    }

    @EventHandler
    public void onPlayerChat(AsyncPlayerChatEvent event) {
        Player player = event.getPlayer();
        UUID playerUUID = player.getUniqueId();

        if (!waitingForHomeName.containsKey(playerUUID)) {
            return;
        }

        event.setCancelled(true);
        waitingForHomeName.remove(playerUUID);

        String message = event.getMessage().trim();

        if (message.equalsIgnoreCase("cancel")) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.loading")));
            return;
        }

        plugin.getServer().getScheduler().runTask(plugin, () -> processSetHomeFromGUI(player, message));
    }

    private void processSetHomeFromGUI(Player player, String homeName) {
        if (!homeManager.isValidHomeName(homeName)) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.invalid-name")));
            return;
        }

        if (!hasPermissionToSetHome(player)) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.no-permission")));
            return;
        }

        homeManager.getHome(player, homeName).thenAccept(existingHome -> {
            if (existingHome != null) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    messageManager.getMessage("home.already-exists").replace("%home%", homeName)));
                return;
            }

            homeManager.getPlayerHomes(player).thenAccept(homes -> {
                int maxHomes = homeManager.getMaxHomesForPlayer(player);
                if (homes.size() >= maxHomes) {
                    player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.max-homes-reached")));
                    return;
                }

                homeManager.createHome(player, homeName, player.getLocation()).thenAccept(success -> {
                    if (success) {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                            messageManager.getMessage("home.set-success").replace("%home%", homeName)));
                    } else {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.error")));
                    }
                });
            });
        });
    }

    private boolean hasPermissionToSetHome(Player player) {
        if (player.hasPermission("cbsystem.home.set.*")) {
            return true;
        }

        for (int i = 1; i <= 28; i++) {
            if (player.hasPermission("cbsystem.home.set." + i)) {
                return true;
            }
        }

        return false;
    }

    public boolean isWaitingForHomeName(UUID playerUUID) {
        return waitingForHomeName.containsKey(playerUUID);
    }

    public void removeWaitingPlayer(UUID playerUUID) {
        waitingForHomeName.remove(playerUUID);
    }
}

package de.nicerecord.citybuildsystem.home;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.utils.ConfigManager;
import de.nicerecord.citybuildsystem.utils.GUIManager;
import de.nicerecord.citybuildsystem.utils.MessageManager;
import net.milkbowl.vault.economy.Economy;
import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.ClickType;
import org.bukkit.event.inventory.InventoryClickEvent;
import org.bukkit.event.player.AsyncPlayerChatEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;

import java.util.HashMap;
import java.util.Map;
import java.util.UUID;

public class HomeGUIListener implements Listener {
    private final CitybuildSystem plugin;
    private final HomeManager homeManager;
    private final ConfigManager configManager;
    private final GUIManager guiManager;
    private final MessageManager messageManager;
    private final Economy economy;
    private final SetHomeCommand setHomeCommand;
    private final Map<UUID, Boolean> waitingForHomeName = new HashMap<>();

    public HomeGUIListener(CitybuildSystem plugin, HomeManager homeManager, ConfigManager configManager,
                          GUIManager guiManager, MessageManager messageManager, Economy economy, SetHomeCommand setHomeCommand) {
        this.plugin = plugin;
        this.homeManager = homeManager;
        this.configManager = configManager;
        this.guiManager = guiManager;
        this.messageManager = messageManager;
        this.economy = economy;
        this.setHomeCommand = setHomeCommand;
    }

    @EventHandler
    public void onInventoryClick(InventoryClickEvent event) {
        if (!(event.getWhoClicked() instanceof Player)) return;

        Player player = (Player) event.getWhoClicked();
        String guiTitle = ChatColor.translateAlternateColorCodes('&', guiManager.getHomeGUITitle());

        if (!event.getView().getTitle().equals(guiTitle)) return;

        event.setCancelled(true);

        ItemStack clickedItem = event.getCurrentItem();
        if (clickedItem == null || clickedItem.getType() == Material.AIR) return;

        ItemMeta meta = clickedItem.getItemMeta();
        if (meta == null || meta.getDisplayName() == null) return;

        String displayName = meta.getDisplayName();

        // Handle close button
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getCloseName()))) {
            player.closeInventory();
            return;
        }

        // Handle navigation - Previous page
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getPreviousPageName()))) {
            int currentPage = getCurrentPageFromInstance(player);
            if (currentPage > 1) {
                HomeGUI homeGUI = new HomeGUI(plugin, homeManager, configManager, guiManager, messageManager, economy);
                homeGUI.openHomesGUI(player, currentPage - 1);
            }
            return;
        }

        // Handle navigation - Next page
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getNextPageName()))) {
            int currentPage = getCurrentPageFromInstance(player);
            int configMaxHomes = configManager.getMaxHomes(); // Verwende Config-Maximum
            int totalPages = (int) Math.ceil((double) configMaxHomes / 28); // HOMES_PER_PAGE
            if (currentPage < totalPages) {
                HomeGUI homeGUI = new HomeGUI(plugin, homeManager, configManager, guiManager, messageManager, economy);
                homeGUI.openHomesGUI(player, currentPage + 1);
            }
            return;
        }

        // Handle info item (do nothing)
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getInfoName()))) {
            return;
        }

        // Handle empty home slots
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeEmptyName()))) {
            player.closeInventory();

            if (!hasPermissionToSetHome(player)) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.no-permission")));
                return;
            }

            waitingForHomeName.put(player.getUniqueId(), true);
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.enter-name")));
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.enter-name-cancel")));
            return;
        }

        // Handle locked home slots (both old and new buy-next-home items)
        if (displayName.equals(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeLockedName())) ||
            displayName.contains("SLOT") && displayName.contains("KAUFEN")) {
            if (!configManager.isEconomyEnabled() || economy == null) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("bank.economy-not-available")));
                return;
            }

            int currentMaxHomes = homeManager.getMaxHomesForPlayer(player);
            int configMaxHomes = configManager.getMaxHomes();

            // Prfe ob bereits das Maximum erreicht ist
            if (currentMaxHomes >= configMaxHomes) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.max-homes-purchased")));
                return;
            }

            double price = configManager.getHomePrice();

            // Prfe ob genug Geld vorhanden ist
            if (economy.getBalance(player) < price) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    messageManager.getMessage("home.insufficient-funds").replace("%price%", String.valueOf((int) price))));
                return;
            }

            // Fhre den Kauf durch
            homeManager.purchaseHomeSlot(player, economy).thenAccept(success -> {
                Bukkit.getScheduler().runTask(plugin, () -> {
                    if (success) {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                            messageManager.getMessage("home.purchase-success").replace("%price%", String.valueOf((int) price))));

                        // Schliee das GUI und ffne es erneut um die nderungen zu zeigen
                        player.closeInventory();
                        Bukkit.getScheduler().runTaskLater(plugin, () -> {
                            HomeGUI homeGUI = new HomeGUI(plugin, homeManager, configManager, guiManager, messageManager, economy);
                            int currentPage = homeGUI.getCurrentPage(player);
                            homeGUI.openHomesGUI(player, currentPage);
                        }, 5L);
                    } else {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.purchase-failed")));
                    }
                });
            });
            return;
        }

        // Handle existing home items
        if (displayName.startsWith(ChatColor.translateAlternateColorCodes('&', guiManager.getHomeSetName().replace("%name%", "")))) {
            String homeName = ChatColor.stripColor(displayName).replace("HOME ", "");

            if (event.getClick() == ClickType.LEFT) {
                player.closeInventory();
                plugin.getServer().dispatchCommand(player, "home " + homeName);
            } else if (event.getClick() == ClickType.RIGHT) {
                homeManager.deleteHome(player, homeName).thenAccept(success -> {
                    if (success) {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                            messageManager.getMessage("home.delete-success").replace("%home%", homeName)));
                        plugin.getServer().getScheduler().runTaskLater(plugin, () -> {
                            HomeGUI homeGUI = new HomeGUI(plugin, homeManager, configManager, guiManager, messageManager, economy);
                            int currentPage = homeGUI.getCurrentPage(player);
                            homeGUI.openHomesGUI(player, currentPage);
                        }, 1L);
                    } else {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.error")));
                    }
                });
            }
        }
    }

    @EventHandler
    public void onPlayerChat(AsyncPlayerChatEvent event) {
        Player player = event.getPlayer();
        UUID playerUUID = player.getUniqueId();

        if (!waitingForHomeName.containsKey(playerUUID)) {
            return;
        }

        event.setCancelled(true);
        waitingForHomeName.remove(playerUUID);

        String message = event.getMessage().trim();

        if (message.equalsIgnoreCase("cancel")) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.loading")));
            return;
        }

        plugin.getServer().getScheduler().runTask(plugin, () -> processSetHomeFromGUI(player, message));
    }

    private void processSetHomeFromGUI(Player player, String homeName) {
        if (!homeManager.isValidHomeName(homeName)) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.invalid-name")));
            return;
        }

        if (!hasPermissionToSetHome(player)) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.no-permission")));
            return;
        }

        homeManager.getHome(player, homeName).thenAccept(existingHome -> {
            if (existingHome != null) {
                player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                    messageManager.getMessage("home.already-exists").replace("%home%", homeName)));
                return;
            }

            homeManager.getPlayerHomes(player).thenAccept(homes -> {
                int maxHomes = homeManager.getMaxHomesForPlayer(player);
                if (homes.size() >= maxHomes) {
                    player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("home.max-homes-reached")));
                    return;
                }

                homeManager.createHome(player, homeName, player.getLocation()).thenAccept(success -> {
                    if (success) {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                            messageManager.getMessage("home.set-success").replace("%home%", homeName)));
                    } else {
                        player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.error")));
                    }
                });
            });
        });
    }

    private boolean hasPermissionToSetHome(Player player) {
        if (player.hasPermission("cbsystem.home.set.*")) {
            return true;
        }

        for (int i = 1; i <= 28; i++) {
            if (player.hasPermission("cbsystem.home.set." + i)) {
                return true;
            }
        }

        return false;
    }

    public boolean isWaitingForHomeName(UUID playerUUID) {
        return waitingForHomeName.containsKey(playerUUID);
    }

    public void removeWaitingPlayer(UUID playerUUID) {
        waitingForHomeName.remove(playerUUID);
    }

    private int getCurrentPageFromInstance(Player player) {
        // Verwende die statische Map aus der HomeGUI-Klasse
        // Da playerPages jetzt static ist, knnen wir eine temporre HomeGUI-Instanz erstellen
        HomeGUI tempGUI = new HomeGUI(plugin, homeManager, configManager, guiManager, messageManager, economy);
        return tempGUI.getCurrentPage(player);
    }
}

package de.nicerecord.citybuildsystem.home;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import org.bukkit.Location;
import org.bukkit.entity.Player;

import java.io.File;
import java.sql.*;
import java.util.*;
import java.util.concurrent.CompletableFuture;

public class HomeManager {
    private final CitybuildSystem plugin;
    private Connection connection;

    public HomeManager(CitybuildSystem plugin) {
        this.plugin = plugin;
        initDatabase();
    }

    private void initDatabase() {
        try {
            File dataFolder = plugin.getDataFolder();
            if (!dataFolder.exists()) {
                dataFolder.mkdirs();
            }

            String dbPath = new File(dataFolder, plugin.getConfig().getString("database.file")).getAbsolutePath();
            connection = DriverManager.getConnection("jdbc:sqlite:" + dbPath);

            String createTable = "CREATE TABLE IF NOT EXISTS homes (" +
                    "id INTEGER PRIMARY KEY AUTOINCREMENT," +
                    "player_uuid TEXT NOT NULL," +
                    "name TEXT NOT NULL," +
                    "world TEXT NOT NULL," +
                    "x REAL NOT NULL," +
                    "y REAL NOT NULL," +
                    "z REAL NOT NULL," +
                    "yaw REAL NOT NULL," +
                    "pitch REAL NOT NULL," +
                    "UNIQUE(player_uuid, name)" +
                    ")";

            try (PreparedStatement stmt = connection.prepareStatement(createTable)) {
                stmt.execute();
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("Fehler beim Initialisieren der Datenbank: " + e.getMessage());
        }
    }

    public CompletableFuture<Boolean> createHome(Player player, String name, Location location) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "INSERT INTO homes (player_uuid, name, world, x, y, z, yaw, pitch) VALUES (?, ?, ?, ?, ?, ?, ?, ?)";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, player.getUniqueId().toString());
                    stmt.setString(2, name);
                    stmt.setString(3, location.getWorld().getName());
                    stmt.setDouble(4, location.getX());
                    stmt.setDouble(5, location.getY());
                    stmt.setDouble(6, location.getZ());
                    stmt.setFloat(7, location.getYaw());
                    stmt.setFloat(8, location.getPitch());
                    stmt.executeUpdate();
                    return true;
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Erstellen des Homes: " + e.getMessage());
                return false;
            }
        });
    }

    public CompletableFuture<Boolean> deleteHome(Player player, String name) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "DELETE FROM homes WHERE player_uuid = ? AND name = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, player.getUniqueId().toString());
                    stmt.setString(2, name);
                    int affected = stmt.executeUpdate();
                    return affected > 0;
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Lschen des Homes: " + e.getMessage());
                return false;
            }
        });
    }

    public CompletableFuture<Home> getHome(Player player, String name) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "SELECT * FROM homes WHERE player_uuid = ? AND name = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, player.getUniqueId().toString());
                    stmt.setString(2, name);
                    try (ResultSet rs = stmt.executeQuery()) {
                        if (rs.next()) {
                            return new Home(
                                    player.getUniqueId(),
                                    rs.getString("name"),
                                    rs.getString("world"),
                                    rs.getDouble("x"),
                                    rs.getDouble("y"),
                                    rs.getDouble("z"),
                                    rs.getFloat("yaw"),
                                    rs.getFloat("pitch")
                            );
                        }
                    }
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Abrufen des Homes: " + e.getMessage());
            }
            return null;
        });
    }

    public CompletableFuture<List<Home>> getPlayerHomes(Player player) {
        return CompletableFuture.supplyAsync(() -> {
            List<Home> homes = new ArrayList<>();
            try {
                String sql = "SELECT * FROM homes WHERE player_uuid = ? ORDER BY name";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, player.getUniqueId().toString());
                    try (ResultSet rs = stmt.executeQuery()) {
                        while (rs.next()) {
                            homes.add(new Home(
                                    player.getUniqueId(),
                                    rs.getString("name"),
                                    rs.getString("world"),
                                    rs.getDouble("x"),
                                    rs.getDouble("y"),
                                    rs.getDouble("z"),
                                    rs.getFloat("yaw"),
                                    rs.getFloat("pitch")
                            ));
                        }
                    }
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Abrufen der Homes: " + e.getMessage());
            }
            return homes;
        });
    }

    public int getMaxHomesForPlayer(Player player) {
        int maxHomes = 0;
        for (int i = 1; i <= plugin.getConfig().getInt("homes.max-homes"); i++) {
            if (player.hasPermission("cbsystem.home.set." + i) || player.hasPermission("cbsystem.home.set.*")) {
                maxHomes = i;
            }
        }
        return maxHomes;
    }

    public boolean isValidHomeName(String name) {
        return name.matches("^[a-zA-Z0-9]+$") && name.length() <= 16;
    }

    public void close() {
        try {
            if (connection != null && !connection.isClosed()) {
                connection.close();
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("Fehler beim Schlieen der Datenbankverbindung: " + e.getMessage());
        }
    }
}

package de.nicerecord.citybuildsystem.home;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import net.milkbowl.vault.economy.Economy;
import org.bukkit.Location;
import org.bukkit.entity.Player;

import java.io.File;
import java.sql.*;
import java.util.*;
import java.util.concurrent.CompletableFuture;

public class HomeManager {
    private final CitybuildSystem plugin;
    private Connection connection;

    public HomeManager(CitybuildSystem plugin) {
        this.plugin = plugin;
        initDatabase();
    }

    private void initDatabase() {
        try {
            File dataFolder = plugin.getDataFolder();
            if (!dataFolder.exists()) {
                dataFolder.mkdirs();
            }

            String dbPath = new File(dataFolder, plugin.getConfig().getString("database.file")).getAbsolutePath();
            connection = DriverManager.getConnection("jdbc:sqlite:" + dbPath);

            String createTable = "CREATE TABLE IF NOT EXISTS homes (" +
                    "id INTEGER PRIMARY KEY AUTOINCREMENT," +
                    "player_uuid TEXT NOT NULL," +
                    "name TEXT NOT NULL," +
                    "world TEXT NOT NULL," +
                    "x REAL NOT NULL," +
                    "y REAL NOT NULL," +
                    "z REAL NOT NULL," +
                    "yaw REAL NOT NULL," +
                    "pitch REAL NOT NULL," +
                    "UNIQUE(player_uuid, name)" +
                    ")";

            try (PreparedStatement stmt = connection.prepareStatement(createTable)) {
                stmt.execute();
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("Fehler beim Initialisieren der Datenbank: " + e.getMessage());
        }
    }

    public CompletableFuture<Boolean> createHome(Player player, String name, Location location) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "INSERT INTO homes (player_uuid, name, world, x, y, z, yaw, pitch) VALUES (?, ?, ?, ?, ?, ?, ?, ?)";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, player.getUniqueId().toString());
                    stmt.setString(2, name);
                    stmt.setString(3, location.getWorld().getName());
                    stmt.setDouble(4, location.getX());
                    stmt.setDouble(5, location.getY());
                    stmt.setDouble(6, location.getZ());
                    stmt.setFloat(7, location.getYaw());
                    stmt.setFloat(8, location.getPitch());
                    stmt.executeUpdate();
                    return true;
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Erstellen des Homes: " + e.getMessage());
                return false;
            }
        });
    }

    public CompletableFuture<Boolean> deleteHome(Player player, String name) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "DELETE FROM homes WHERE player_uuid = ? AND name = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, player.getUniqueId().toString());
                    stmt.setString(2, name);
                    int affected = stmt.executeUpdate();
                    return affected > 0;
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Lschen des Homes: " + e.getMessage());
                return false;
            }
        });
    }

    public CompletableFuture<Home> getHome(Player player, String name) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                String sql = "SELECT * FROM homes WHERE player_uuid = ? AND name = ?";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, player.getUniqueId().toString());
                    stmt.setString(2, name);
                    try (ResultSet rs = stmt.executeQuery()) {
                        if (rs.next()) {
                            return new Home(
                                    player.getUniqueId(),
                                    rs.getString("name"),
                                    rs.getString("world"),
                                    rs.getDouble("x"),
                                    rs.getDouble("y"),
                                    rs.getDouble("z"),
                                    rs.getFloat("yaw"),
                                    rs.getFloat("pitch")
                            );
                        }
                    }
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Abrufen des Homes: " + e.getMessage());
            }
            return null;
        });
    }

    public CompletableFuture<List<Home>> getPlayerHomes(Player player) {
        return CompletableFuture.supplyAsync(() -> {
            List<Home> homes = new ArrayList<>();
            try {
                String sql = "SELECT * FROM homes WHERE player_uuid = ? ORDER BY name";
                try (PreparedStatement stmt = connection.prepareStatement(sql)) {
                    stmt.setString(1, player.getUniqueId().toString());
                    try (ResultSet rs = stmt.executeQuery()) {
                        while (rs.next()) {
                            homes.add(new Home(
                                    player.getUniqueId(),
                                    rs.getString("name"),
                                    rs.getString("world"),
                                    rs.getDouble("x"),
                                    rs.getDouble("y"),
                                    rs.getDouble("z"),
                                    rs.getFloat("yaw"),
                                    rs.getFloat("pitch")
                            ));
                        }
                    }
                }
            } catch (SQLException e) {
                plugin.getLogger().severe("Fehler beim Abrufen der Homes: " + e.getMessage());
            }
            return homes;
        });
    }

    public int getMaxHomesForPlayer(Player player) {
        int maxHomes = 0;
        for (int i = 1; i <= plugin.getConfig().getInt("homes.max-homes"); i++) {
            if (player.hasPermission("cbsystem.home.set." + i) || player.hasPermission("cbsystem.home.set.*")) {
                maxHomes = i;
            }
        }
        return maxHomes;
    }

    public CompletableFuture<Boolean> purchaseHomeSlot(Player player, Economy economy) {
        return CompletableFuture.supplyAsync(() -> {
            try {
                // Aktuelle maximale Homes des Spielers
                int currentMaxHomes = getMaxHomesForPlayer(player);
                int configMaxHomes = plugin.getConfig().getInt("homes.max-homes");

                // Prfe ob Spieler bereits das Maximum erreicht hat
                if (currentMaxHomes >= configMaxHomes) {
                    return false;
                }

                // Berechne den Preis fr das nchste Home-Slot
                double price = plugin.getConfig().getDouble("homes.home-price");

                // Prfe ob Spieler genug Geld hat
                if (economy.getBalance(player) < price) {
                    return false;
                }

                // Ziehe das Geld ab
                if (!economy.withdrawPlayer(player, price).transactionSuccess()) {
                    return false;
                }

                // Gebe dem Spieler die Berechtigung fr ein zustzliches Home
                int nextHomeSlot = currentMaxHomes + 1;

                // Verwende LuckPerms oder PermissionsEx API wenn verfgbar
                // Fr dieses Beispiel verwenden wir den Bukkit Permission Manager (temporr)
                player.addAttachment(plugin, "cbsystem.home.set." + nextHomeSlot, true);

                // TODO: Fr permanente Berechtigungen sollte hier eine Integration
                // mit LuckPerms oder einem anderen Permission-Plugin erfolgen

                return true;
            } catch (Exception e) {
                plugin.getLogger().severe("Fehler beim Kauf eines Home-Slots: " + e.getMessage());
                return false;
            }
        });
    }

    public int getNextHomeSlotPrice() {
        return (int) plugin.getConfig().getDouble("homes.home-price");
    }

    public boolean isValidHomeName(String name) {
        return name.matches("^[a-zA-Z0-9]+$") && name.length() <= 16;
    }

    public void close() {
        try {
            if (connection != null && !connection.isClosed()) {
                connection.close();
            }
        } catch (SQLException e) {
            plugin.getLogger().severe("Fehler beim Schlieen der Datenbankverbindung: " + e.getMessage());
        }
    }
}

package de.nicerecord.citybuildsystem.manager;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.data.CityPlayer;
import org.bukkit.Location;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.configuration.file.YamlConfiguration;
import org.bukkit.entity.Player;

import java.io.File;
import java.io.IOException;
import java.util.HashMap;
import java.util.Map;
import java.util.UUID;

public class PlayerManager {

    private final CitybuildSystem plugin;
    private final Map<UUID, CityPlayer> players;
    private final File playersFile;
    private FileConfiguration playersConfig;

    public PlayerManager(CitybuildSystem plugin) {
        this.plugin = plugin;
        this.players = new HashMap<>();
        this.playersFile = new File(plugin.getDataFolder(), "players.yml");

        createPlayersFile();
        loadPlayers();
    }

    private void createPlayersFile() {
        if (!playersFile.exists()) {
            playersFile.getParentFile().mkdirs();
            try {
                playersFile.createNewFile();
            } catch (IOException e) {
                plugin.getLogger().severe("Fehler beim Erstellen der players.yml: " + e.getMessage());
            }
        }
        playersConfig = YamlConfiguration.loadConfiguration(playersFile);
    }

    private void loadPlayers() {
        if (!playersConfig.contains("players")) return;

        for (String playerId : playersConfig.getConfigurationSection("players").getKeys(false)) {
            UUID uuid = UUID.fromString(playerId);
            String path = "players." + playerId;

            CityPlayer cityPlayer = new CityPlayer(uuid);

            // Homes laden
            if (playersConfig.contains(path + ".homes")) {
                for (String homeName : playersConfig.getConfigurationSection(path + ".homes").getKeys(false)) {
                    String homePath = path + ".homes." + homeName;
                    Location home = new Location(
                        plugin.getServer().getWorld(playersConfig.getString(homePath + ".world")),
                        playersConfig.getDouble(homePath + ".x"),
                        playersConfig.getDouble(homePath + ".y"),
                        playersConfig.getDouble(homePath + ".z"),
                        (float) playersConfig.getDouble(homePath + ".yaw"),
                        (float) playersConfig.getDouble(homePath + ".pitch")
                    );
                    cityPlayer.setHome(homeName, home);
                }
            }

            players.put(uuid, cityPlayer);
        }
    }

    public void saveAllPlayers() {
        for (CityPlayer player : players.values()) {
            savePlayer(player);
        }
        try {
            playersConfig.save(playersFile);
        } catch (IOException e) {
            plugin.getLogger().severe("Fehler beim Speichern der Spielerdaten: " + e.getMessage());
        }
    }

    private void savePlayer(CityPlayer cityPlayer) {
        String path = "players." + cityPlayer.getUuid().toString();

        // Homes speichern
        for (Map.Entry<String, Location> entry : cityPlayer.getHomes().entrySet()) {
            String homePath = path + ".homes." + entry.getKey();
            Location home = entry.getValue();

            playersConfig.set(homePath + ".world", home.getWorld().getName());
            playersConfig.set(homePath + ".x", home.getX());
            playersConfig.set(homePath + ".y", home.getY());
            playersConfig.set(homePath + ".z", home.getZ());
            playersConfig.set(homePath + ".yaw", home.getYaw());
            playersConfig.set(homePath + ".pitch", home.getPitch());
        }
    }

    public CityPlayer getCityPlayer(UUID uuid) {
        return players.computeIfAbsent(uuid, CityPlayer::new);
    }

    public CityPlayer getCityPlayer(Player player) {
        return getCityPlayer(player.getUniqueId());
    }

    public void setHome(Player player, String homeName, Location location) {
        CityPlayer cityPlayer = getCityPlayer(player);
        cityPlayer.setHome(homeName, location);
        savePlayer(cityPlayer);
    }

    public Location getHome(Player player, String homeName) {
        CityPlayer cityPlayer = getCityPlayer(player);
        return cityPlayer.getHome(homeName);
    }

    public boolean hasHome(Player player, String homeName) {
        CityPlayer cityPlayer = getCityPlayer(player);
        return cityPlayer.hasHome(homeName);
    }

    public void removeHome(Player player, String homeName) {
        CityPlayer cityPlayer = getCityPlayer(player);
        cityPlayer.removeHome(homeName);
        savePlayer(cityPlayer);
    }
}

package de.nicerecord.citybuildsystem.teleport;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.utils.MessageManager;
import org.bukkit.ChatColor;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class TpHereCommand implements CommandExecutor {
    private final CitybuildSystem plugin;
    private final MessageManager messageManager;

    public TpHereCommand(CitybuildSystem plugin, MessageManager messageManager) {
        this.plugin = plugin;
        this.messageManager = messageManager;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!(sender instanceof Player)) {
            sender.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.only-players")));
            return true;
        }

        Player player = (Player) sender;

        if (!player.hasPermission("cbsystem.tphere")) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.no-permission")));
            return true;
        }

        if (args.length != 1) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("commands.tphere.usage")));
            return true;
        }

        Player target = plugin.getServer().getPlayer(args[0]);
        if (target == null) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                messageManager.getMessage("general.player-not-found").replace("%player%", args[0])));
            return true;
        }


        return true;
    }
}

package de.nicerecord.citybuildsystem.teleport;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import de.nicerecord.citybuildsystem.utils.MessageManager;
import org.bukkit.ChatColor;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class TpHereCommand implements CommandExecutor {
    private final CitybuildSystem plugin;
    private final MessageManager messageManager;

    public TpHereCommand(CitybuildSystem plugin, MessageManager messageManager) {
        this.plugin = plugin;
        this.messageManager = messageManager;
    }

    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!(sender instanceof Player)) {
            sender.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.only-players")));
            return true;
        }

        Player player = (Player) sender;

        if (!player.hasPermission("cbsystem.tphere")) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("general.no-permission")));
            return true;
        }

        if (args.length != 1) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("commands.tphere.usage")));
            return true;
        }

        Player target = plugin.getServer().getPlayer(args[0]);
        if (target == null) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&',
                messageManager.getMessage("general.player-not-found").replace("%player%", args[0])));
            return true;
        }

        if (target.equals(player)) {
            player.sendMessage(ChatColor.translateAlternateColorCodes('&', messageManager.getMessage("social.message-self")));
            return true;
        }

        target.teleport(player.getLocation());
        player.sendMessage(ChatColor.translateAlternateColorCodes('&',
            messageManager.getMessage("teleport.tphere-teleported").replace("%player%", target.getName())));
        target.sendMessage(ChatColor.translateAlternateColorCodes('&',
            messageManager.getMessage("teleport.tphere-teleported-by").replace("%player%", player.getName())));

        return true;
    }
}

package de.nicerecord.citybuildsystem.teleport;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import org.bukkit.entity.Player;
import org.bukkit.scheduler.BukkitRunnable;

import java.util.*;
import java.util.concurrent.ConcurrentHashMap;

public class TpaManager {
    private final CitybuildSystem plugin;
    private final Map<UUID, TpaRequest> pendingRequests = new ConcurrentHashMap<>();
    private final Set<UUID> tpaDisabledPlayers = ConcurrentHashMap.newKeySet();
    private BukkitRunnable cleanupTask;

    public TpaManager(CitybuildSystem plugin) {
        this.plugin = plugin;
        startCleanupTask();
    }

    public boolean sendTpaRequest(Player requester, Player target, TpaRequest.TpaType type) {
        // Check if target has TPA disabled
        // Check if target has TPA disabled
        if (tpaDisabledPlayers.contains(target.getUniqueId())) {
            return false;
        }
        // Remove any existing request from this requester

        // Create new request
        // Remove any existing request from this requester
        pendingRequests.remove(requester.getUniqueId());

        // Create new request
        TpaRequest request = new TpaRequest(requester, target, type);
        pendingRequests.put(requester.getUniqueId(), request);

        return true;
    }

    public TpaRequest getPendingRequest(UUID playerUUID) {
        return pendingRequests.get(playerUUID);
    }

    public TpaRequest getRequestForTarget(UUID targetUUID) {
        for (TpaRequest request : pendingRequests.values()) {
            if (request.getTarget().equals(targetUUID)) {
                return request;
            }
        }
        return null;
    }

    public void removeRequest(UUID requesterUUID) {
        pendingRequests.remove(requesterUUID);
    }

    public void removePendingRequestsForTarget(UUID targetUUID) {
        pendingRequests.entrySet().removeIf(entry -> entry.getValue().getTarget().equals(targetUUID));
    }

    public boolean isTpaEnabled(UUID playerUUID) {
        return !tpaDisabledPlayers.contains(playerUUID);
    }

            // Remove all pending requests for this player
    public void toggleTpa(UUID playerUUID) {
        if (tpaDisabledPlayers.contains(playerUUID)) {
            tpaDisabledPlayers.remove(playerUUID);
        } else {
            tpaDisabledPlayers.add(playerUUID);
            // Remove all pending requests for this player
            removePendingRequestsForTarget(playerUUID);
        }
    }

    private void startCleanupTask() {
        cleanupTask = new BukkitRunnable() {
        // Run every 10 seconds
            @Override
            public void run() {
                int timeoutSeconds = plugin.getConfigManager().getTpaTimeout();
                pendingRequests.entrySet().removeIf(entry ->
                    entry.getValue().isExpired(timeoutSeconds));
            }
        };
        // Run every 10 seconds
        cleanupTask.runTaskTimer(plugin, 200L, 200L);
    }

    public void shutdown() {
        if (cleanupTask != null) {
            cleanupTask.cancel();
        }
    }

    public Collection<TpaRequest> getAllRequests() {
        return pendingRequests.values();
    }
}

package de.nicerecord.citybuildsystem.teleport;

import de.nicerecord.citybuildsystem.CitybuildSystem;
import org.bukkit.entity.Player;
import org.bukkit.scheduler.BukkitRunnable;

import java.util.*;
import java.util.concurrent.ConcurrentHashMap;

public class TpaManager {
    private final CitybuildSystem plugin;
    private final Map<UUID, TpaRequest> pendingRequests = new ConcurrentHashMap<>();
    private final Set<UUID> tpaDisabledPlayers = ConcurrentHashMap.newKeySet();
    private BukkitRunnable cleanupTask;

    public TpaManager(CitybuildSystem plugin) {
        this.plugin = plugin;
        startCleanupTask();
    }

    public boolean sendTpaRequest(Player requester, Player target, TpaRequest.TpaType type) {
        if (tpaDisabledPlayers.contains(target.getUniqueId())) {
            return false;
        }

        pendingRequests.remove(requester.getUniqueId());

        TpaRequest request = new TpaRequest(requester, target, type);
        pendingRequests.put(requester.getUniqueId(), request);

        return true;
    }

    public TpaRequest getPendingRequest(UUID playerUUID) {
        return pendingRequests.get(playerUUID);
    }

    public TpaRequest getRequestForTarget(UUID targetUUID) {
        for (TpaRequest request : pendingRequests.values()) {
            if (request.getTarget().equals(targetUUID)) {
                return request;
            }
        }
        return null;
    }

    public void removeRequest(UUID requesterUUID) {
        pendingRequests.remove(requesterUUID);
    }

    public void removePendingRequestsForTarget(UUID targetUUID) {
        pendingRequests.entrySet().removeIf(entry -> entry.getValue().getTarget().equals(targetUUID));
    }

    public boolean isTpaEnabled(UUID playerUUID) {
        return !tpaDisabledPlayers.contains(playerUUID);
    }

    public void toggleTpa(UUID playerUUID) {
        if (tpaDisabledPlayers.contains(playerUUID)) {
            tpaDisabledPlayers.remove(playerUUID);
        } else {
            tpaDisabledPlayers.add(playerUUID);
            removePendingRequestsForTarget(playerUUID);
        }
    }

    private void startCleanupTask() {
        cleanupTask = new BukkitRunnable() {
            @Override
            public void run() {
                int timeoutSeconds = plugin.getConfigManager().getTpaTimeout();
                pendingRequests.entrySet().removeIf(entry ->
                    entry.getValue().isExpired(timeoutSeconds));
            }
        };
        cleanupTask.runTaskTimer(plugin, 200L, 200L);
    }

    public void shutdown() {
        if (cleanupTask != null) {
            cleanupTask.cancel();
        }
    }

    public Collection<TpaRequest> getAllRequests() {
        return pendingRequests.values();
    }
}

package de.nicerecord.citybuildsystem.teleport;

import org.bukkit.entity.Player;

import java.util.UUID;

public class TpaRequest {
    private final UUID requester;
    private final UUID target;
    private final TpaType type;
    private final long timestamp;

    public TpaRequest(Player requester, Player target, TpaType type) {
        this.requester = requester.getUniqueId();
        this.target = target.getUniqueId();
        this.type = type;
        this.timestamp = System.currentTimeMillis();
    }

    public UUID getRequester() {
        return requester;
    }

    public UUID getTarget() {
        return target;
    }

    public TpaType getType() {
        return type;
    }

    public long getTimestamp() {
        return timestamp;
    }

    public boolean isExpired(int timeoutSeconds) {
        return System.currentTimeMillis() - timestamp > (timeoutSeconds * 1000L);
    }

    public enum TpaType {
        TPA,        // Requester wants to teleport TO target
        TPAHERE     // Requester wants target to teleport to THEM
    }
}

